parameters.functionCode:
// Validar los datos de entrada
const documentData = $input.json.document;
const userData = $input.json.user_data;
const timestamp = $input.json.timestamp;

// Verificar que existen datos del documento
if (!documentData || !documentData.url || !documentData.file || !documentData.document_type) {
  return {
    valid: false,
    error: 'Informaci贸n del documento incompleta'
  };
}

// Verificar tipo de archivo permitido
const allowedMimeTypes = ['application/pdf', 'image/jpeg', 'image/png', 'image/jpg'];
if (!allowedMimeTypes.includes(documentData.type)) {
  return {
    valid: false,
    error: 'Tipo de archivo no permitido. Solo se permiten PDF, JPG o PNG.'
  };
}

// Extraer extensi贸n del archivo
const fileExtension = documentData.file.split('.').pop().toLowerCase();
const allowedExtensions = ['pdf', 'jpg', 'jpeg', 'png'];
if (!allowedExtensions.includes(fileExtension)) {
  return {
    valid: false,
    error: `Extensi贸n de archivo no permitida: ${fileExtension}. Solo se permiten PDF, JPG o PNG.`
  };
}

// Todo OK
return {
  valid: true,
  documentData,
  userData,
  timestamp,
  source: $input.json.source,
  form_id: $input.json.form_id,
  fileExtension
};

// Procesar el documento seg煤n su tipo
const docType = $input.json.documentData.document_type;
const documentId = $input.json.insertId || $input.json.id || $input.json.lastInsertRowid;
const filePath = $input.json.documentData.file;
const fileUrl = $input.json.documentData.url;
const mimeType = $input.json.documentData.type;

// Clasificar el documento seg煤n su tipo
let classification = '';
let priority = 'normal';
let nextAction = '';

switch (docType) {
  case 'id':
    classification = 'Identificaci贸n';
    priority = 'alta';
    nextAction = 'verificar_identidad';
    break;
  case 'income':
    classification = 'Comprobante de ingresos';
    priority = 'alta';
    nextAction = 'analisis_financiero';
    break;
  case 'tax':
    classification = 'Declaraci贸n de impuestos';
    priority = 'media';
    nextAction = 'analisis_fiscal';
    break;
  case 'bank':
    classification = 'Extracto bancario';
    priority = 'media';
    nextAction = 'analisis_financiero';
    break;
  case 'property':
    classification = 'Documentaci贸n de propiedad';
    priority = 'alta';
    nextAction = 'tasacion';
    break;
  default:
    classification = 'Otro';
    priority = 'baja';
    nextAction = 'revision_general';
}

return {
  documentId,
  filePath,
  fileUrl,
  mimeType,
  docType,
  classification,
  priority,
  nextAction,
  needsReview: true,
  processed: true
};

