parameters.functionCode:
// Validar los datos de entrada
const formData = $input.json.form_data;
const userData = $input.json.user_data;
const timestamp = $input.json.timestamp;

// Verificar campos obligatorios
const requiredFields = ['nombre', 'apellidos', 'email', 'telefono', 'importe', 'plazo'];
const missingFields = [];

for (const field of requiredFields) {
  if (!formData[field]) {
    missingFields.push(field);
  }
}

if (missingFields.length > 0) {
  return {
    valid: false,
    missingFields,
    error: `Faltan campos obligatorios: ${missingFields.join(', ')}`
  };
}

// Validar formato de email
const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
if (!emailRegex.test(formData.email)) {
  return {
    valid: false,
    error: 'El formato del email no es v√°lido'
  };
}

// Validar formato de tel√©fono (simplificado)
const phoneRegex = /^[0-9]{9,15}$/;
if (!phoneRegex.test(formData.telefono.replace(/\s+/g, ''))) {
  return {
    valid: false,
    error: 'El formato del tel√©fono no es v√°lido'
  };
}

// Validar importe
const importe = parseFloat(formData.importe);
if (isNaN(importe) || importe <= 0) {
  return {
    valid: false,
    error: 'El importe debe ser un n√∫mero positivo'
  };
}

// Validar plazo
const plazo = parseInt(formData.plazo);
if (isNaN(plazo) || plazo <= 0) {
  return {
    valid: false,
    error: 'El plazo debe ser un n√∫mero entero positivo'
  };
}

// Todo OK
return {
  valid: true,
  formData,
  userData,
  timestamp,
  source: $input.json.source,
  form_id: $input.json.form_id
};

